frappe.ui.form.on('Hotel Guests', {
    first_name: function(frm) {
        set_guest_name(frm);
    },
    middle_name: function(frm) {
        set_guest_name(frm);
    },
    last_name: function(frm) {
        set_guest_name(frm);
    }
});

function set_guest_name(frm) {
    // Combine first name, middle name, and last name conditionally
    let full_name = [frm.doc.first_name, frm.doc.middle_name, frm.doc.last_name]
        .filter(name => name) // Remove empty values
        .join(' '); // Join remaining names with a single space

    // Set the guest_name field
    frm.set_value('guest_name', full_name);
}



