frappe.ui.form.on('Hotel Guests', {
    is_corporate: function(frm) {
        toggle_first_name_label(frm);
    },
    refresh: function(frm) {
        toggle_first_name_label(frm);  // Ensure the label is updated on form load
    }
});

function toggle_first_name_label(frm) {
    if (frm.doc.is_corporate) {
        // Change label to 'Corporate Name' when checkbox is checked
        frm.fields_dict['first_name'].df.label = "Corporate Name";
        frm.fields_dict['nationality'].df.label = "Country";
        frm.fields_dict['guest_information_section'].df.label = "Corporate Information";
        frm.fields_dict['permanent_address'].df.label = "Address";
        frm.fields_dict['work_phone_no'].df.label = "Corporate Phone NO";
        frm.fields_dict['contact_no'].df.label = "Contact Person NO";
    } else {
        // Change label back to 'First Name' when checkbox is unchecked
        frm.fields_dict['first_name'].df.label = "First Name";
        frm.fields_dict['nationality'].df.label = "Nationality";
        frm.fields_dict['guest_information_section'].df.label = "Guest Information";
        frm.fields_dict['permanent_address'].df.label = "Permanent Address";
        frm.fields_dict['work_phone_no'].df.label = "Work Phone No";
        frm.fields_dict['contact_no'].df.label = "Contact No";
    }
    frm.refresh_field('first_name');  // Refresh the field to apply the label change
    frm.refresh_field('nationality');
    frm.refresh_field('guest_information_section');
    frm.refresh_field('permanent_address');
    frm.refresh_field('work_phone_no');
    frm.refresh_field('contact_no');
}

