frappe.ui.form.on('Hotel Check In', {
    rental_type: function(frm) {
        if (frm.doc.rental_type === 'Daily') {
            frm.set_value('period_nights', 1);
        } else if (frm.doc.rental_type === 'Monthly') {
            frm.set_value('period_nights', 30);
        }
        frm.trigger('set_checkout_date');  // Trigger the function to set checkout date
    },
    period_nights: function(frm) {
        frm.trigger('set_checkout_date');  // Trigger the function to set checkout date
    },
    check_in: function(frm) {
        frm.trigger('set_checkout_date');  // Trigger the function to set checkout date
    },
    set_checkout_date: function(frm) {
        if (frm.doc.check_in && frm.doc.period_nights) {
            // Calculate the checkout date
            let check_in_date = frappe.datetime.add_days(frm.doc.check_in, frm.doc.period_nights);
            frm.set_value('check_out', check_in_date);
        }
    }
});

frappe.ui.form.on('Hotel Check In', {
    refresh: function(frm) {
        // Rename 'Save' button to 'Reservation' for new records
        if (frm.is_new()) {
            frm.page.btn_primary[0].innerText = __('Reservation');
        }

        // Rename 'Submit' button to 'Check-in' after the form is saved (before submission)
        if (!frm.is_new() && frm.doc.docstatus === 0) {
            frm.page.set_primary_action(__('Check-in'), function() {
                frm.save('Submit');
            });
        }
    }
});

frappe.ui.form.on('Hotel Check In Room', {
    check_out: function (frm, cdt, cdn) {
        // Get the clicked row data
        let row = locals[cdt][cdn];
        
        // Redirect to Hotel Check Out doctype
        frappe.route_options = {
            room: row.room_no,
            customer: row.guest_name,// Pass the room field value (replace 'room' with the actual fieldname if different)
        };
        frappe.set_route('Form', 'Hotel Check Out', 'new');
    }
});

frappe.ui.form.on('Rooms', {
    room_status: function (frm) {
        // Get the current room status from the form
        const currentStatus = frm.doc.room_status;
        
        // Log for debugging
        console.log("Current room status:", currentStatus);
        console.log("Previous room status:", frm._last_room_status);
        
        // Only proceed if the room status has changed
        if (frm._last_room_status !== currentStatus) {
            // Define image paths for different statuses
            const imagePaths = {
                "Available": "/files/available.jpg",
                "Checked In": "/files/checked_in.jpg",
                "Reserved": "/files/reserved.jpg",
                "Room Service": "/files/room_service.jpg",
                "Maintenance": "/files/maintenance.jpg"
            };

            // Check if the current status has a corresponding image path
            if (imagePaths[currentStatus]) {
                // Set the image path in the form
                frm.set_value('image', imagePaths[currentStatus]);
                
                // Log for debugging
                console.log("Image set to:", imagePaths[currentStatus]);
            } else {
                // If no image path is found, clear the image field
                frm.set_value('image', null);
                console.log("No image found for status:", currentStatus);
            }

            // Refresh the image field to reflect the change
            frm.refresh_field('image');

            // Update the last known room status to the current status
            frm._last_room_status = currentStatus;
        }
    },

    // Ensure the last room status is initialized correctly on load
    onload: function (frm) {
        // Initialize the last room status to the current room status (or default if empty)
        frm._last_room_status = frm.doc.room_status || "Available";  // Default to "Available" if no status is set
        console.log("Initialized last room status:", frm._last_room_status);
    },

    // Trigger room_status function when the document is loaded or refreshed
    refresh: function (frm) {
        frm.trigger('room_status');
    }
});
